import React, { useEffect } from 'react';
import imagePath from './utils/imagePath';
import './Blog.css';

const blogPosts = [
  {
    id: 1,
    image: '_DSC9182.jpg',
    title: 'Steel Will in Glacier',
    text: 'Sometimes the best shots happen when you least expect them. This Steel Will knife caught the morning light perfectly against the Glacier National Park backdrop. The contrast between human craftsmanship and raw nature never gets old.'
  },
  {
    id: 2,
    image: '_DSC1672-2.jpg',
    title: 'Golden Hour Magic',
    text: 'There\'s something about that last hour of light that transforms everything. The way it catches the edges, softens the shadows, and makes even the simplest subjects feel cinematic.'
  },
  {
    id: 3,
    image: '_DSC4869-3.jpg',
    title: 'Details Matter',
    text: 'In commercial work, it\'s often the smallest details that tell the biggest stories. Every texture, every reflection, every shadow has purpose.'
  },
  {
    id: 4,
    image: '_DSC2842.jpg',
    title: 'Tools of the Trade',
    text: 'Gear that works when it matters. This headlamp has seen more adventures than most people, and it shows in every scratch and scuff.'
  },
  {
    id: 5,
    image: '_DSC9596-2.jpg',
    title: 'Wild Spaces',
    text: 'The best office in the world doesn\'t have walls. Just endless sky, clean air, and the kind of silence that makes you remember what matters.'
  },
  {
    id: 6,
    image: '_DSC1974.jpg',
    title: 'Simplicity',
    text: 'Sometimes the most powerful images are the quietest ones. No tricks, no gimmicks—just honest light and authentic moments.'
  }
];

const Blog = () => {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <div className="blog-container">
      <div className="blog-header">
        <h1>Field Notes</h1>
        <p>Stories from behind the lens</p>
      </div>

      <div className="blog-content">
        {blogPosts.map((post, index) => (
          <article key={post.id} className={`blog-post ${index % 2 === 1 ? 'reverse' : ''}`}>
            <div className="post-image">
              <img 
                src={imagePath(post.image)} 
                alt={post.title}
                loading="lazy"
              />
            </div>
            <div className="post-content">
              <h2>{post.title}</h2>
              <p>{post.text}</p>
            </div>
          </article>
        ))}
      </div>

      <div className="blog-footer">
        <p>More stories coming soon...</p>
      </div>
    </div>
  );
};

export default Blog;
